float f_n_dist_line_segment_byiq(
    in vec2 p,
    in vec2 a,
    in vec2 b
) {
	vec2 ba = b - a;
	vec2 pa = p - a;
	float h = clamp(dot(pa, ba) / dot(ba, ba), 0., 1.);
	return length(pa - h * ba);
}

float f_n_inout(float n){
    if(n > .5){
        return 0.5 - mod(n, 0.5); 
    }
    return n;

}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 o_trpino = (fragCoord - iResolution.xy*0.5)/ iResolution.y;
    vec2 o_fc_nor = fragCoord.xy / iResolution.xy;
    o_trpino*=sin(iTime)*2.;
    float n_tau = 6.2831;
    vec3 o_col = vec3(1.);//0.12,0.3,0.8);
    vec2 o_p = vec2(0., 0.);
    vec2 o_diff = o_trpino-o_p;
    float n_dist = length(o_diff);
    float n_ang = (atan(o_diff.x, o_diff.y)+(n_tau/2.))/n_tau;
    vec4 o = vec4((sin(n_ang*n_tau*10.)*sin(fract(n_dist+iTime)*n_tau)+0.5));
    fragColor = o*vec4(o_col, 1.);

}
