void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float timescale = 12.0;
    float countdown = mod(iTime, timescale);

    float fractalcolor = 0.0; vec2 Res = iResolution.xy;
    vec2 uv = (fragCoord *2.0 - Res.xy)/Res.y;
    float zoom = (0.5 + 0.5 *countdown);
    uv = uv.xy / (zoom * countdown * 0.5);

    float creal = 0.36 + countdown/400.0;
    float cimag = -0.1;
    float real = uv.x;
    float imag = uv.y;
    float iterations = 200.0;
    for(float i = 0.0; i < iterations; i++)
    {
        float realtemp = real;
        real = real*real - imag*imag + creal;
        imag = 2.0 * realtemp * imag + cimag;

        if(sqrt(real*real + imag*imag) > 4.0)
            fractalcolor = (i/iterations) *1.5; 
    }

    fragColor = vec4(fractalcolor + 0.05,fractalcolor,fractalcolor+0.3,1.0);  
}
