void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   float x = fragCoord.x * iResolution.y/iResolution.x;
   float x1 = smoothstep(x, x+10.0, fragCoord.y);
   float x2 = smoothstep(x-10.0, x, fragCoord.y);
   float x3 = x2 - x1;
   fragColor = vec4(1.0, 1.0, 1.0, 1.0)*x3 + vec4(0.0, 0.0, 0.0, 1.0)*(1.0-x3);
}
