#define TMIN 0.1
#define TMAX 20.
#define RAYMARCH_TIME 128
#define PRECISION .001
#define AA 3
#define R 1.5

vec2 fixUV(vec2 uv) {
    return (2. * uv - iResolution.xy) / min(iResolution.x, iResolution.y);
}
float sdfSphere(vec3 p) {
    return length(p - vec3(0., 0, 2.)) - R;
}
vec3 calcNormal(in vec3 p) {
    const float h = 0.0001;
    const vec2 k = vec2(1, -1);
    return normalize(k.xyy * sdfSphere(p + k.xyy * h) +
        k.yyx * sdfSphere(p + k.yyx * h) +
        k.yxy * sdfSphere(p + k.yxy * h) +
        k.xxx * sdfSphere(p + k.xxx * h));
}
float rayMarch(vec3 ro, vec3 rd) {
    float t = TMIN;
    for(int i = 0; i < RAYMARCH_TIME && t < TMAX; i++) {
        vec3 p = ro + t * rd;
        float d = sdfSphere(p);
        if(d < PRECISION) {
            break;
        }
        t += d;
    }
    return t;
}
vec3 render(vec2 uv) {
    vec3 color = vec3(0.);
    vec3 ro = vec3(0., 0., -1.5);
    vec3 rd = normalize(vec3(uv, 0.) - ro);
    float t = rayMarch(ro, rd);
    if(t < TMAX) {
        vec3 p = t * rd + ro;
        // vec3 light = vec3(0.7, 0., -1);
        vec3 light = vec3(2. * cos(iTime - 2.0), 0.4, 2. * sin(iTime - 2.0) + 2.);
        vec3 n = calcNormal(p);
        float diff = clamp(dot(normalize(light - p), n), 0., 1.);
        vec3 amb = 0.5 + 0.5 * dot(n, vec3(0., 1., 0.)) * vec3(cos(iTime), sin(iTime), 1.);
        // vec3 amb = vec3(0.,0,1.);
        return diff * vec3(sin(iTime), 0., cos(iTime)) + amb * vec3(0.5);
    }
    return sqrt(color);
}
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fixUV(fragCoord);

    vec3 color = vec3(0.);
    for(int m = 0; m < AA; m++) {
        for(int n = 0; n < AA; n++) {
            vec2 offset = 2. * (vec2(float(m), float(n)) / float(AA) - .5);
            vec2 uv = fixUV(fragCoord + offset);
            color += render(uv);
        }
    }
    fragColor = vec4(color / float(AA * AA), 1.);
}
