// Spatial Aliasing, mla, 2023.
// A nice example from Keenan Crane

const float PI = 3.141592654;
const float TWOPI = 2.0*PI;

void mainImage(out vec4 fragColor, vec2 fragCoord){
    vec2 z = fragCoord-0.5*iResolution.xy;
    z *= 2.0/iResolution.y;
    if (iMouse.z > 0.0) z *= exp2(20.0*iMouse.x/iResolution.x);
    else z *= pow(1.0+0.5*iTime,2.0);
    float t = mod(dot(z,z),TWOPI);
    vec3 col = vec3(sin(t+0.5*vec3(0,2,4)));
    fragColor = vec4(col,1.0);
}
