void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // centered uv (-1. ~ 1.)
    vec2 uv = (fragCoord/iResolution.xy) * 2.0 - 1.0;

    // mouse input
    float mx = 2.0; //(iMouse.x / iResolution.x) * 2.0;
    float my = 4.0; //(iMouse.y / iResolution.y) * 2.0;


    // polar coordinates
    vec2 uv2 = vec2(atan(uv.y, uv.x)*mx, length(uv)*my);

    // move
    uv2.y += iTime;


    // texture
    vec3 col = texture(iChannel0, uv2).rgb;
    fragColor = vec4(col, 1.0);
}
