void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // UV manipulation
    vec2 uv = AspectRatioUV(fragCoord.xy, iResolution.xy);

    //Parameter
    float time = iTime;
    float blur = 0.01;

    float d;

    //Visu
    vec3 pix = vec3(.5);

    d = smoothstep(0.,blur,sdBox(uv, vec2(.1,.2),vec2(.3,-.05))-.2);
    pix = mix(vec3(.5,.04,.22), pix, d);

    d = smoothstep(0.,blur,sdBox(uv, vec2(.01,.1),vec2(.175,-.6))-.12);
    pix = mix(vec3(.5,.04,.22), pix, d);

    d = smoothstep(0.,blur,sdBox(uv, vec2(.01,.1),vec2(-.175,-.6))-.12);
    pix = mix(vec3(.77,.11f,.11), pix, d);

    d = smoothstep(0.,blur,sdSegment(uv, vec2(.0,.25),vec2(.0,-.25))-.4);
    pix = mix(vec3(.77,.11f,.11), pix, d);

    d = smoothstep(0.,blur,sdSegment(uv, vec2(.15,.2),vec2(-.15,.2))-.15);
    pix = mix(vec3(.56,.79,.86), pix, d);

	fragColor = vec4(pix, 1.0);
}

//Constant
#define PI  3.14159265359
#define TAU 6.28318530717

//Color
