///Inigo Quilez Signed Distance Field
//https://iquilezles.org/articles/distfunctions2d
///Playlist shadertoy SDF : 
//https://www.shadertoy.com/playlist/MXdSRf&from=36&num=12
float sdCircle( vec2 p, float r )
{
    return length(p) - r;
}
float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float sdBox( in vec2 p, in vec2 b, in vec2 o)
{
    vec2 d = abs(p-o)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}
float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

///UV (TextCoord)
vec2 CenterUV(vec2 fragCoord, vec2 resolution)
{
    ///Centering Operation
	vec2 uv = vec2( fragCoord.xy - 0.5 * resolution.xy );
	// [0, iResolution.x] -> [-0.5*iResolution.x, 0.5*iResolution.x]
	// [0, iResolution.y] -> [-0.5*iResolution.y, 0.5*iResolution.y]
    return uv;
}
vec2 FullScreenUV(vec2 fragCoord, vec2 resolution)
{	
    vec2 uv = CenterUV(fragCoord.xy, resolution);
    uv = 2.0 * uv.xy / resolution.xy;
	// [-iResolution.x/2, iResolution.x/2] -> [-1.0, 1.0]
    // [-iResolution.y/2, iResolution.y/2] -> [-1.0, 1.0]

    return uv;
}
vec2 AspectRatioUV(vec2 fragCoord, vec2 resolution)
{	
    vec2 uv = CenterUV(fragCoord.xy, resolution);
	uv = 2.0 * uv.xy / resolution.y;
    // AspectRatio = 1920/1080 = 1.778
    // [-iResolution.x/2, iResolution.x/2] ->[-1.778, 1.778]
	// [-iResolution.y/2, iResolution.y/2] ->[-1, 1]
    return uv;
}
vec2 RotateUV(vec2 uv, float angle)
{	
    //angle in degree (TAU = 2PI = 360 degrees)
	angle = angle * PI / 180.0;
    return uv * mat2( cos(angle), sin(angle),
                     -sin(angle), cos(angle));
}

///COLOR
vec3 Color255(int R, int G, int B)
{
    float rat = 1.0/255.0;
    return vec3(float(R)*rat, float(G)*rat, float(B)*rat);
}
//Sources: https://gist.github.com/yiwenl/745bfea7f04c456e0101, https://gist.github.com/sugi-cho/6a01cae436acddd72bdf
vec3 HSV2RGB(vec3 c){
    vec4 K = vec4(1.0, 2.0/3.0, 1.0/3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}
//Source: https://gist.github.com/sugi-cho/6a01cae436acddd72bdf
vec3 RGB2HSV(vec3 c){
	vec4 K=vec4(0.,-1./3.,2./3.,-1.),
	     p=mix(vec4(c.bg ,K.wz),vec4(c.gb,K.xy ),step(c.b,c.g)),
	     q=mix(vec4(p.xyw,c.r ),vec4(c.r ,p.yzx),step(p.x,c.r));
	float d=q.x-min(q.w,q.y),
	      e=1e-10;
	return vec3(abs(q.z+(q.w-q.y)/(6.*d+e)),d/(q.x+e),q.x);
}
vec3 HueShiftcolor(vec3 c, float shift)
{
    c = RGB2HSV(c);
    c = vec3(mod(c.x + shift,1.), c.yz);
    vec4 K = vec4(1.0, 2.0/3.0, 1.0/3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

