precision highp float;

uniform sampler2D imageTex;

uniform float time;



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
#define resolution iResolution
#define time iTime
    vec2 newCoord = uv;
    newCoord = vec2(newCoord.x * cos(time) - newCoord.y * sin(time), newCoord.x * sin(time) + newCoord.y * cos(time));
    newCoord = vec2(newCoord.x / resolution.x, newCoord.y / resolution.y);
    newCoord = (newCoord - 0.5) * vec2(2.0, 2.0);
    newCoord = vec2(newCoord.x * cos(time) - newCoord.y * sin(time), newCoord.x * sin(time) + newCoord.y * cos(time));
    newCoord = (newCoord + 0.5) / vec2(2.0, 2.0);
    newCoord = vec2(newCoord.x * resolution.x, newCoord.y * resolution.y);
    vec4 color = texture(imageTex, newCoord);
    fragColor = color;

}
