// golfing 433 chars experiment https://shadertoy.com/view/4tG3Wy
// based on https://www.shadertoy.com/view/ltjXWW

void mainImage( out vec4 O, vec2 U ) {
    vec2  R = iResolution.xy ;
    float L = 20., A = 1.7, i=0., k,s;

	for ( O*=0.; i++ < L; A += A )                    // loop on harmonics
		s = exp2( k = mod( i - 1.5*iTime, L ) ),      // fractal noise spectrum
		O += ( 1. - cos(6.3*k/L) ) / s                // enveloppe                    // try 1e3,2e3
           * (.15 - abs( texture(iChannel0, s*(U+U-R)/R.y* mat2(cos(A+vec4(0,11,33,0))) /2e3 ).r - .5));

    O = O / fwidth(O) +.7;
}
