vec2 triangle_wave(vec2 a){
    //a -= .5;
    return abs(fract((a+vec2(1.,0.5))*1.5)-.5);
}

vec3 image1(vec2 uv,float sign1){
    float scale = 1.5;
    vec3 col = vec3(0.);
    bool t3 = false;
    uv /= 8.*8.;
    for(int k = 0; k < 21; k++){
        t3 = uv.x > uv.y || !t3;
        //uv *= -1.;
        uv =
            triangle_wave(uv-.5)/1.5+triangle_wave(uv.yx)
        ;
        //uv *= -1.;
        //if(uv.y>uv.x){uv=uv.yx;}
        col.x = uv.y-uv.x;
        if(t3) {col = col.yzx;uv=uv.yx;}
    }
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    float t1 = 16.*4.;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    fragColor = vec4(abs(image1(uv,1.))*2.,1.0);
}

