int D2(vec2 p, float n) {
    float x = float(int(floor(n / 10.0f)) % 10);
    float y = float(int(n) % 10);

    return clamp(D(p, x), 0, 1) + clamp(D(vec2(p.x - 4.0f, p.y), y), 0, 1);
