#define TILE_SIZE 100.0f

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    if (int(fragCoord.x) % int(TILE_SIZE) == 0 || int(fragCoord.y) % int(TILE_SIZE) == 0)
    {
        fragColor = vec4(0);
        return;
    }

    // Output to screen
    fragColor = vec4(col,1.0);

    vec2 coord = fragCoord;
    coord %= TILE_SIZE;
    coord /= 10.0f;
    coord.x -= 1.0f;

    float n = floor(fragCoord.x / TILE_SIZE) + floor(fragCoord.y / TILE_SIZE);
    //n %= 10.0f;
    //int mask = clamp(D(coord, n),0, 1);
    int mask = clamp(D2(coord, n), 0, 1);

    fragColor += vec4(mask);
}
