float hash(vec3 p)  
{
    return fract(sin(dot(p,vec3(71.53,13.91,43.63)))*137935.23);
}

float noise (in vec2 p)
{
    vec2 h = fract(p);
    h = h*h*(3.-2.*h);
    p=floor(p);
    float v1 = hash(vec3(p,0.0));
    float v2 = hash(vec3(p.x+1.0,p.y,0.0));
    float v3 = hash(vec3(p.x,p.y+1.0,0.0));
    float v4 = hash(vec3(p+1.0,0.0));
    float k1 = mix(v1,v2,h.x);
    float k2 = mix(v3,v4,h.x);
    return mix(k1,k2,h.y);
}

float fbm (in vec2 p)
{
    return dot(vec4(noise(p), noise(p*2.0), noise(p*4.0), noise(p*8.0)),vec4(0.5, 0.25, 0.125, 0.0625));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.yy;



    vec3 col = vec3(fbm(uv*8.0));


    fragColor = vec4(col,1.0);
}
