#define pi 3.14159
struct Wave{
    vec2 pos; // wave source position
    float T; // period
    float lambda; // wavelength
};

float getVal(Wave w, vec2 uv){
    return sin(2.*pi*(iTime/w.T - length(uv-w.pos)/w.lambda));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.x; 
    vec2 mousePos = iMouse.xy/iResolution.x;
    Wave w1 = Wave(vec2(.3,.3),.9,.05);
    Wave w2 = Wave(mousePos,.9,.05);
    float val = (2.+getVal(w1,uv) + getVal(w2,uv))/4.;
    fragColor = vec4(val*vec3(1),1.0);
}
