#define size 1.0

float l(vec2 uv) {
    float l = max(0.8-length(uv),-0.1);
    return max(sin(min(l,0.5)) + 0.05*sin(25.*min(l,0.3))+0.1, 0.05);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 coord = fragCoord.xy / iResolution.xy;;
    vec2 uv = (2.*fragCoord-iResolution.xy) / iResolution.y / size;

    vec3 light = vec3(l(uv*1.04), l(uv*1.02+vec2(0.,0.002)), l(uv+vec2(0.,0.004)));
    vec3 color = texture(iChannel0, coord, 0.).rgb * light / size;

    fragColor = vec4(color, 1.0);
}
