void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uv_r, uv_g, uv_b;
    uv_r = uv;
    uv_g = uv;
    uv_b = uv;


    //add color glitch stripes
    if(int(uv.y / 0.05f) % 2 == 0){
        uv_r.x += + 0.05f;     
    }

    if(int(uv.y+0.002f / 0.05f) % 2 == 0){
        uv_g.x -= 0.03f;     
    }

    //iChannel0 texture
    vec4 pic_r = texture(iChannel0,uv_r);
    vec4 pic_g = texture(iChannel0,uv_g);
    vec4 pic_b = texture(iChannel0,uv_b);


    // Output to screen
    fragColor = vec4(pic_r.r,pic_g.g,pic_b.b, 1.0f);

    //color correction
    fragColor += vec4(-0.4f,-.7f,-0.1f,0.0f);

    //old tv/monitor vibe
    fragColor = fragColor + 0.1f*sin(uv.y*700.0f);

    //vignette
    uv *=  1.0 - uv.yx;
    float vig = uv.x*uv.y * 15.0; // multiply with sth for intensity
    vig = pow(vig, 0.2); // change pow for modifying the extend of the  vignette
    fragColor *= vec4(vig); 


}
