void mainImage( out vec4 O, in vec2 u )
{
    vec2 uv = u / iResolution.xy,
    g = u.xy;

    float s = 20.,
    zoom = 2.,
    k = .5;

    uv.y = 1. - uv.y;
    // not ideal
    if(uv.y > .25){
      zoom *= k;
    }
    if(uv.y > .5){
      zoom *= k;
    }
    if(uv.y > .75){
      zoom *= k;
    }

    g *= zoom;
    g.x += round(fract(g.y / s) ) * s *0.5;
    float m = round(fract(g.x / s));
    O = vec4(m);
}
