void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;

    vec3 color = vec3(0.0);

    float t = iTime * 1.0;
    for(int i=0; i<5; i++)
    {
        vec2 stripe = vec2(1.0 / 5.0, 0.0);
        vec2 p = uv + t + stripe * float(i);
        p = fract(p) * 2.0 - 1.0;

        float c = 0.5 / length(p);
        c = pow(c, 8.0);
        c = sin(c * 10.0 + t * 2.0);

        vec3 stripeColor = vec3(sin(p.x * 2.0), cos(p.y * 2.0), sin(t));
        stripeColor = pow(stripeColor, vec3(3.0));
        color += c * stripeColor;
    }

    color = pow(color, vec3(0.25));
    color = mix(vec3(0.5), color, sin(t * 0.5));
    fragColor = vec4(color, 1.0);
}
