void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
  uv.x *= iResolution.x / iResolution.y;
  vec2 st = uv * 5.0;

  // Set the initial color to light blue
  vec3 bodyColor = vec3(0.4, 0.6, 1.0);

  // Penguin body
  float body = length(st - vec2(0.0, -0.8));
  float head = length(st - vec2(0.0, 0.5));
  float eye = length(st - vec2(0.2, 0.7));
  float iris = length(st - vec2(0.2, 0.75));
  float tummy = length(st - vec2(0.0, -0.4));
  float foot1 = length(st - vec2(0.3, -1.1));
  float foot2 = length(st - vec2(-0.3, -1.1));

  vec3 tummyColor = vec3(1.0, 1.0, 1.0);
  vec3 eyeColor = vec3(1.0, 1.0, 1.0);

  if (body < 1.0) {
    bodyColor = vec3(0.0, 0.0, 0.0);
  }

  if (tummy < 0.5) {
    bodyColor = tummyColor;
  }

  if (head < 0.7) {
    bodyColor = vec3(0.0, 0.0, 0.0);
  }

  if (eye < 0.1) {
    bodyColor = vec3(0.0, 0.0, 0.0);
  }

  if (iris < 0.05) {
    bodyColor = vec3(1.0, 1.0, 1.0);
  }

  if (foot1 < 0.1 || foot2 < 0.1) {
    bodyColor = vec3(0.0, 0.0, 0.0);
  }

  // Output the color of the pixel
  fragColor = vec4(bodyColor, 1.0);
}
