mat2 rot(float angle)
{
    float c = cos(angle);
    float s = sin(angle);
    return mat2(c, -s, s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float scale = iResolution.x/iResolution.y;
    float w = 1./iResolution.y;

    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;    
    uv.x *= scale;
    uv *= rot(iTime);

    uv *= 2.;

    vec3 col = vec3(0.);

    float angle = atan(uv.x, uv.y);
    float d = length(uv);

    float curve = d - sin(3.0 * angle);
    float c = smoothstep(w*10.0,-w,curve);
    col = mix(col,vec3(1.0,0.,0.),c);

    // Output to screen
    fragColor = vec4(col,1.0);
}
