vec3 palette( float t ) {
vec3 a = vec3(0.995, 0.182, 0.874);
vec3 b = vec3(0.493, 0.024, 0.920);
vec3 c = vec3(1.407, 1.347, 0.139);
vec3 d = vec3(4.219, 2.346, 4.313);

return a + b * cos(6.28318 * (c * t + d));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;

    vec3 finalColor = vec3(0.0);

    for (float i = 0.0; i < 2.0; i++) {

        uv = fract(uv * (1.5 + i)) - 0.5;

        float d = sin(cos(-length(uv)) * exp(sin(length(uv0))));

        vec3 col = palette(length(uv0) + iTime * 0.8);

        d = pow(0.01 / abs(cos(d*10. - iTime * 1.2)/12.), 1.8);

        finalColor += col * d;

    }

    fragColor = vec4(finalColor, 1.0);
}
