float f(float i){return (i*-1.)+1.;}
void mainImage(out vec4 fragColor,in vec2 fragCoord){
    fragCoord.x += iTime*40.;
    fragCoord.y += (iTime/2.)*40.;
    vec2 pos = floor(fragCoord/90.);
    vec2 ppos = mod(floor((fragCoord/90.)*18.),18.);
    float col = mod(pos.x + mod(pos.y, 2.0), 2.0);
    if (((ppos.y == 1. || ppos.y == 16.) && ppos.x >= 1. && ppos.x < 17.) ||
        ((ppos.x == 1. || ppos.x == 16.) && ppos.y >= 2. && ppos.y < 16.)){ col=f(col); }
    if (ppos.x >= 8. && ppos.x < 10. && ppos.y >= 8. && ppos.y < 10.){col=f(col);}
    fragColor = vec4((col*(26./255.))+(65./255.));
}
