#define I iResolution
void mainImage( out vec4 U, in vec2 V )
{
    vec2 u = V/I.xy;

    float p, i,  k = .2, l = 25.;

    for( ; i < l; i++ ){

        u.x += cos( i - k
        ) * k * sin( u.y ) * i;
        u.y += sin( k ) * i * sin( u.x ) * i;

        p = mix(
            p,
            i,
            ( dot( u.x, u.x ) * l ) - k * i
        );

    }

    U += p;
}
