void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float w = 1.0 / iResolution.y;
    float scale = iResolution.x / iResolution.y;

    vec2 uv = fragCoord / iResolution.xy;
    uv -= 0.5;
    uv.x *= scale;

    uv.x=abs(uv.x);
    uv.y *= 1.2;
    uv.y -= uv.x*sqrt((10.-uv.x*15.)/20.);

    //uv.y -= uv.x;

    vec3 col = vec3(0.0);

    float r = 0.4 + pow(sin(iTime*4. + uv.y*3.5)*0.2+0.2,4.5);
    float d = length(uv) - r;
    float c = smoothstep(w, -w, d);
    col = mix(col, vec3(1,0,0),c);

    // Output to screen
    fragColor = vec4(col,1.0);
}
