#define PI 3.14159265359
#define PI2 6.28318530718
#define BPM 127.
#define BPS BPM/60.
#define S01(t) sin(t) * .5 + .5
#define C01(t) cos(t) * .5 + .5

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // time
    float t = iTime*BPS;
    float t8 = t/8.;
    float t16 = t/16.;


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float scale = 50.;


    // coords
    vec2 gv = fract(uv*scale)-.5;
    vec2 id = floor(uv*scale);
    vec2 idNormal = id / scale;


    // texture
    vec4 tSamp = texture(iChannel0, uv);
   tSamp = texture(iChannel1, idNormal);



    // convert to black and white
    // (sum of r g b channels)
    float bw = (tSamp.x + tSamp.y + tSamp.z)/ mix(.25, 15., S01(t8));

    // clamp to range between 0 and 1
    bw = clamp(0., 1., bw);


    // circle
    float circle = length(gv);

    // Use the grayscale of an image to control the circle size.
    // Circle size is determined by the step cut off value.
    circle = step(circle, 0.5*bw);


    // Output to screen
    vec3 col = vec3(circle);
    fragColor = vec4(col, 1.);
}
