void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 res = iResolution.xy;
    vec2 U = (fragCoord/res-0.5)*vec2(6.2831,3);
    U.x += iTime;

    //Real sine for comparison (yellow)
    float y1 = sin(U.x);

    //Fake sine using Cubic Hermite Spline (blue)
    #define PI 3.141592653
    float a = abs(mod(U.x/PI-.5,2.)-1.);
    float y2 = a*a*(3.-2.*a)*2.-1.;

    fragColor = (0.01-abs(vec4(y1,y1,y2,0)-U.y))*res.y*.5;
}
