void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    vec3 c = vec3(0);

    c = texture(iChannel0, uv).rgb;
    c = vec3((c.r + c.g + c.b) / 3.0);

    vec3 k = vec3(0.9, 0.4, 0.9);
    vec3 l = vec3(0.8, 0.8, 0.9);

    c = smoothstep(0.45, 0.55, c);
    c = smoothstep(1.0, 0.5, c);
    c = mix(k, l, c);

    fragColor = vec4(c, 1.0);
}
