void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(1.0,0,0) ;

    vec2 gv = fract( uv*5.)-.5;

    float d = length(gv);
    float m = smoothstep(.4,.3,d);

    col.rg = gv;
    col += m;

    fragColor = vec4(col, 2.0);
}
