#define TAU 6.28318530718

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;
    float time = iTime * 0.3;
    float s = sin(uv.x * 10.0 + time) + cos(uv.y * 10.0 + time);
    fragColor = vec4( vec3(s * 0.5 + 0.5), 1.0 );
}


//This shader takes the screen coordinates of each pixel and adjusts them to be centered 
//at the origin, with the aspect ratio taken into account. It then uses the sin and cos 
//functions to create a pattern that rotates over time, controlled by the time variable. 
//The resulting value is used to determine the color of each pixel, with a final step to 
//set the alpha channel to fully opaque.
