float Box( vec2 p,  float b) {
    p = abs(p)-b;
    return length(max(p,0.)) + min(max(p.x,p.y),0.);
}

#define rot(a) mat2(cos( a + 1.5708*vec4(0,1,-1,0) ) )

void mainImage( out vec4 O, vec2 u )
{
    float t = iTime;
    vec2  R = iResolution.xy,
          U0 = abs( u - .5*R ) / R.y,
          U = .5 * U0 - .125;
    O *= 0.;  
    for( float i=1.; i<7e2; i++)      
        U *= rot( i* t/ 764.),
        O = mix(O, .5 + 0.5*cos(1./i*t*10. +i +U0.xyxx +vec4(0,2,4,0)),
                   smoothstep(.8/R.y, 0., Box(U,.5/i*5.) ) );
}
