void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(texelFetch(iChannel1,ivec2(fragCoord),0).rgb,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //scroll parameter
    vec2 offset = vec2(1.0, 0.0);

    vec2 uv = fragCoord/iResolution.xy;
    vec3 col;
    if (int(fragCoord.x)+1==int(iResolution.x)){
        //right edge code
        col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    }
    else{
        col = texelFetch(iChannel1,ivec2(fragCoord+offset),0).rgb;
    }
    fragColor = vec4(col,1.0);
}
