void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Convert fragment coordinates into a position within the canvas
    vec2 p = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y/1.5;


    // Calculate the distance from the center of the canvas
    float d = length(p);

    // Calculate the texture coordinates for the circle
    vec2 uv = 1.0 * (p + vec2(1.0));

    // Get the texture color from the input texture
    vec4 tex = texture(iChannel0, uv);

    // Calculate the angle of the current fragment relative to the center
    float angle = atan(p.y, p.x);

    // Calculate the border width of the circle
    float border = abs(0.5 - d) * 8.0;

    // Inner circle
    float sinValue0 = smoothstep(0.49, 0.51, border + 0.1 * sin(10.0 * iTime + 12.0 * angle));
    vec3 color0 = mix(vec3(0.0, 0.0, 1.0), vec3(1.0, 1.0, 1.0), sinValue0);
    vec2 offset0 = vec2(0.03 * sin(10.0), 0.0);

    // Blue stroke
    float sinValue1 = smoothstep(0.49, 0.51, border + 0.1 * sin(10.0 * iTime + 12.0 * angle));
    vec3 color1 = mix(vec3(0.0, 0.0, 1.0), vec3(1.0, 1.0, 1.0), sinValue1);
    vec2 offset1 = vec2(0.03 * sin(2.0 * iTime + 6.0 * angle), 0.0);

    // Purple stroke
    float sinValue2 = smoothstep(0.2, 0.3, border + ( 0.1 * sin(iTime) ) * sin(10.0 * iTime + 12.0 * angle));
    vec3 color2 = mix(vec3(.8, 2.0, .8), vec3(1.0, 1.0, 1.0), sinValue2);
    vec2 offset2 = vec2(0.03 * sin(4.0 * iTime + 8.0 * angle), 0.0);

    // Draw both strokes    
    vec4 blueTex = vec4(color1, 1.0) + tex;
    vec4 purpleTex = vec4(color2, 1.0) + tex;

    vec4 blueMix = mix(blueTex, vec4(color1, 1.0), step(0.5 + offset1.x, d));
    vec4 purpleMix = mix(purpleTex, vec4(color2, 1.0), step(1.0 + offset2.x, d));

    vec4 finalColor = blueMix;
    finalColor = mix(finalColor, purpleMix, 0.5);

    //fragColor = mix(fragColor, vec4(color2, 1.0), step(0.5 + offset2.x, d));


    // Draw texture in the center
    finalColor = mix(tex, finalColor, step(0.4 + offset0.x, d));


    //fragColor = mix(fragColor, vec4(color2, 1.0), step(0.5 + offset1.x, d));

    fragColor = finalColor;
}
