void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 checker = fract( 10.0 * uv );

	float bubbleSize = 0.3;
	float bubbleDist = length( uv - 0.5 ) - bubbleSize;
	float bubble = smoothstep( 0.0, 0.02, bubbleDist );

	float noise = ( sin( uv.y * 10.0 + iTime * 5.0 ) + 1.0 ) * 0.5;
	float wobble = sin( bubbleDist * 20.0 + iTime * 5.0 ) * 0.05;

	float color = ( checker.x > checker.y ) ? 1.0 : 0.0;
	color = mix( color, 0.7, bubble );
	color = mix( color, 1.0, noise * bubble * bubble * 3.0 );

	fragColor = vec4( vec3( color + wobble ), bubble * bubble * 2.0 );
}
