float max_sub = 5.0;

float rand(in vec2 uv) {
    return fract(sin(dot(uv,  vec2(12.9898,78.233))) * 43758.5453123);
}


vec3 RandomS(in vec2 pos) {
    vec2 intPart   = floor(pos);
    vec2 fractPart = fract(pos); 

    float  subNum = float(int((max_sub)*rand(intPart)) + 1);
    vec2   localSquare   = floor(fractPart * subNum);

    return vec3( rand(  localSquare * rand(intPart)),
                 rand( (localSquare + 100.0) *rand(intPart + 100.0) ),
                 rand( (localSquare + 1000.0)* rand(intPart + 1000.0))
    );

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xx;
    uv *= 10.0;

    //max_sub =  (0.5 + 0.5 * sin(iTime * 0.5))*max_sub;
    fragColor = vec4(vec3(RandomS(uv)), 1.0);
}
