float quality = 20.;
float lengthBlur = 0.1;
float POWpower = 1.;
vec2 barrelDistortion(vec2 coord, float amt) {
	vec2 cc = coord - 0.5;
	float dist = dot(cc, cc);
    dist=pow(dist,POWpower);
	return coord - cc * dist  * amt;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    // lengthBlur = sin(iTime)*2.;
    // POWpower = abs(sin(iTime)*3.)+1.;
	vec2 uv=(fragCoord.xy/iResolution.xy);
    vec4 a = vec4(0.);
    int samples = 0;
    if (lengthBlur != 0.){
        if (lengthBlur > 0.){
            for (float i = 0.0; i < lengthBlur; i+=1./quality ){
                a +=texture(iChannel0, barrelDistortion(uv, i));
                samples++;
            }
        }else{
            for (float i = 0.0; i < -lengthBlur; i+=1./quality ){
                a +=texture(iChannel0, barrelDistortion(uv, -i));
                samples++;
            }
        }
        fragColor = a/float(samples);
    }else{
        fragColor = texture(iChannel0, uv);
    }
}
