// Blobs based on @paulofalcao Blobs shader

#define time iTime

                //x dim, y dim,  xOscFreq,  yOscFreq, xOscAmp,  yOscAmp,  time
float makePoint(float x, float y, float fx, float fy, float sx, float sy, float t){
   float xx=x+sin(t*fx)*sx;
   float yy=y+cos(t*fy)*sy;

   //change val for causing effect similar to a zoom in/out && less bloom
   return 1.250/sqrt(xx*xx+yy*yy);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

   vec2 p=(fragCoord.xy/iResolution.x)*2.0-vec2(1.0,iResolution.y/iResolution.x);

   //change val for causing effect similar to a zoom in/out 
   p=p*1.5;

   float x=p.x;
   float y=p.y;

   float timeR = time*0.35;
   float timeB = time*0.6;

   //these points make form the red blobs
   float a=
       makePoint(x*0.6,y*0.55,0.3,0.09,0.53,0.3,time*0.9);

   a=a+makePoint(x,y,1.9,2.0,0.4,0.4,time*0.2);
   a=a+makePoint(x,y,0.8,0.7,0.4,0.5,timeR);
   a=a+makePoint(x*0.3,y*0.35,0.3,0.1,0.6,0.3,timeR);

   a=a+makePoint(x,y,0.8,1.7,0.5,0.4,timeR);
   a=a+makePoint(x,y,0.3,1.0,0.4,0.4,timeR);
   a=a+makePoint(x,y,1.4,1.7,0.4,0.5,timeR);
   a=a+makePoint(x,y,1.3,2.1,0.6,0.3,time*0.1);
   a=a+makePoint(x*0.25,y*0.25,0.48,0.37,0.25,0.24,time);   

   //these points form the green blobs
                            //xdim, ydim, xFreq, yFreq, xAmp, yAmp, time
     float b =     makePoint(x*0.21,y*0.21, 0.22, 0.05, 0.30, 0.30, time);
           b = b + makePoint(x*0.30,y*0.28, 0.70, 2.70, 0.40, 0.40, time*0.15);
           b = b + makePoint(x*0.24,y*0.24, 0.84, 0.60, 0.40, 0.50, time*0.1);
           b = b + makePoint(x*0.26,y*0.26, 0.26, 0.40, 0.60, 0.30, time*0.2);
           b = b + makePoint(x*0.80,y*0.80, 0.70, 0.40, 0.50, 0.74, time*0.25);
           b = b + makePoint(x*1.00,y*1.00, 0.10, 0.70, 1.58, 0.80, time*0.7);
   /*
   b=b+makePoint(x,y,0.8,0.5,0.4,0.5,time);
   b=b+makePoint(x,y,1.4,0.9,0.6,0.3,time);
   b=b+makePoint(x,y,0.7,1.3,0.5,0.4,time);
    */

   //these points form the blue blobs
   float c=
       makePoint(x,y,3.7,0.3,0.3,0.3,timeB);

   c=c+makePoint(x,y,1.9,1.3,0.4,0.4,time);
   c=c+makePoint(x,y,0.8,0.9,0.4,0.5,timeB);
   c=c+makePoint(x,y,1.2,1.7,0.6,0.3,time);
   c=c+makePoint(x,y,0.3,0.6,0.5,0.4,timeB);
   c=c+makePoint(x,y,0.3,0.3,0.4,0.4,time);
   c=c+makePoint(x,y,1.4,0.8,0.4,0.5,timeB);
   c=c+makePoint(x,y,0.2,0.6,0.6,0.3,time*0.1);
   c=c+makePoint(x*0.5,y*0.5,1.3,0.5,0.5,0.4,timeB);

          //change vals to get intensity up or down for RGB (affects blobs "expansion" also)
   vec3 d=vec3(a*0.7,b*0.75,c*1.1)/26.0;

   fragColor = vec4(d.x,d.y-0.75,d.z,1.01)+
               vec4(0.22,-0.1,0.22,1.0);
}
