mat2 rotate2d(float angle){
    return mat2(cos(angle),-sin(angle),
                sin(angle),cos(angle));
}
vec2 randomVector(vec2 pos){

    vec2 tmp = vec2(dot(pos,vec2(280.69,69.420)),
                    dot(pos,vec2(4316.40216,7785.420)));
    return fract(sin(tmp) * 13473.732106) * 2. - 1.;

}
float noise(vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( dot( randomVector(i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ),
                     dot( randomVector(i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( randomVector(i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ),
                     dot( randomVector(i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv *= 10.5;
    uv *= rotate2d(noise(uv / 3.  - iTime / 20.));
    vec2 fcoord = fract(uv);
    // Time varying pixel color

    vec3 col = vec3(0);
    vec2 o = (smoothstep(0.05,0.1,abs(fcoord - .5)));
    col.r = o.x * o.y;
    //Uncomment this for pewdiepie like pattern lmao
   //col.r = (smoothstep(0.1,0.2,fcoord.y));


    // Output to screen
    fragColor = vec4(col,1.0);
}
