void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    vec2 data = texture(iChannel0,uv).xy;
    fragColor.xyz = vec3(0.5+data.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 inColor = texture(iChannel0,fragCoord/iResolution.xy);
    vec4 inColor0 = texture(iChannel0,(fragCoord+vec2(1.,0.))/iResolution.xy);
    vec4 inColor1 = texture(iChannel0,(fragCoord+vec2(0.,1.))/iResolution.xy);
    vec4 inColor2 = texture(iChannel0,(fragCoord+vec2(-1.,0.))/iResolution.xy);
    vec4 inColor3 = texture(iChannel0,(fragCoord+vec2(0.,-1.))/iResolution.xy);
    inColor.y+=((inColor0.x+inColor1.x+inColor2.x+inColor3.x)/4.-inColor.x)*1.0;
    if(iMouse.z>0.){
        if(length(fragCoord-iMouse.xy)<10.){
            inColor.y=1.;  
        }
    }
    inColor.x+=inColor.y;
    fragColor.xy=inColor.xy;
}
