void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float amp = smoothstep(0.05, 0.4, 1.0 - uv.x) * smoothstep(0.05, 0.4, uv.x);
    float smth = 2.0 * smoothstep(0.0, 1.0, 1.2 - uv.x) * smoothstep(0.0, 1.0, uv.x+0.2);

    uv.y += 0.2 * amp * sin(uv.x*37.6 + iTime * 3.5);
    vec3 line1 = vec3(smoothstep(0.4*smth, 0.5, 1.0 - uv.y)) * smoothstep(0.4*smth, 0.5, uv.y);
    vec3 col1 = vec3(0.80*uv.x, 0.70, 0.70*(1.0-uv.x));
    line1 *= col1;

    uv.y += 0.1;
    uv.y += 0.15 * amp * sin(uv.x*39.9 + 1.8 + iTime * 2.5);
    vec3 line2 = vec3(smoothstep(0.4*smth, 0.5, 1.0 - uv.y)) * smoothstep(0.4*smth, 0.5, uv.y);
    vec3 col2 = vec3(0.80*(1.0-uv.x), 0.60, 0.90*(uv.x));
    line2 *= col2;

    fragColor = vec4(line1 + line2, 1.0);
}
