float plot(float r, float pct){
	return  smoothstep( pct-0.5, pct, r) -smoothstep( pct, pct+0.2, r);
}

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d ){
        return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = vec3(6);
    vec2 pos = vec2(6) - uv;
    pos *= cos(iTime)*1.0+0.1;

    float r = length(pos)*8.0;
    float a = atan(pos.y,pos.x);

    float f = abs(cos(a*2.5+iTime*0.1))*sin(iTime*1.0)*0.698+cos(iTime)-4.0;
    float d = f-r;

    col = (vec3(smoothstep(fract(d),fract(d)+-0.100,0.1))-vec3(smoothstep(fract(d),fract(d)+-1.184,0.160)) ) 
	* pal( f, vec3(0.2,0.4,0.4),vec3(0.1,0.0,0.0),vec3(0.1,0.5,0),vec3(0.0,0.1,0.4) );
    float pct = plot(r*0.272,fract(d*(sin(iTime)*0.45+0.5)));

    // Output to screen
    fragColor = vec4(col,1.0);
}
