void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int blur1 = 3;

    vec2 uv = fragCoord/iResolution.xy;

    int x1 = (blur1 * 2) + 1;
    int y1 = x1;

    vec3 total1 = vec3(0.0, 0.0, 0.0);
    while(x1 > 0){
        while(y1 > 0){
            total1 += texture(iChannel0, vec2(fragCoord.x + float(x1) - float(blur1 * 2),
                                             fragCoord.y + float(y1) - float(blur1 * 2))/iResolution.xy).xyz;
            y1 = y1 - 1;
        }
        y1 = (blur1 * 2) + 1;
        x1 = x1 - 1;
    }

    vec3 Color1 = total1 / float(((blur1 * 2) + 1) * ((blur1 * 2) + 1));
    //
    int blur2 = 6;


    int x2 = (blur2 * 2) + 1;
    int y2 = x2;

    vec3 total2 = vec3(0.0, 0.0, 0.0);
    while(x2 > 0){
        while(y2 > 0){
            total2 += texture(iChannel0, vec2(fragCoord.x + float(x2) - float(blur2 * 2),
                                             fragCoord.y + float(y2) - float(blur2 * 2))/iResolution.xy).xyz;
            y2 = y2 - 1;
        }
        y2 = (blur2 * 2) + 1;
        x2 = x2 - 1;
    }

    vec3 Color2 = total2 / float(((blur2 * 2) + 1) * ((blur2 * 2) + 1));

    fragColor= vec4(Color1-Color2,1.);
}
