void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);
}

#define fb iChannel0
#define PI 3.1415926538
#define T iTime*0.5
float sin_lerp(float a,float b,float t){
    float tem=(1.0-cos(t*PI))/2.0;
    return a*(1.0-tem)+b*tem;
}
float random(float seed){
    return fract(sin(seed+1.) * 43758.5453);
}
float curl_random(float seed,float offset,float freq){
    return sin_lerp(random(offset+floor(mod(seed,freq))),random(offset+floor(mod(seed+1.,freq))),abs(seed-floor(seed)));
}
// Code originally from draw_to_fb().
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec4 color=vec4(0.0);
	vec2 uv= fragCoord.xy / iResolution.xy;
    //vec2 offset;
    bool mouse_hold = !(iMouse.z < 0.0);
    vec2 mouse = iMouse.xy;
    if(!mouse_hold)
        mouse=iResolution.xy*(vec2(cos(T*5.0)*0.7,sin(T)*0.8)*0.5+0.5);
    //offset-=vec2(0.5);
    //offset*=0.01;
	color = texture(fb,uv+vec2(0.0,0.03))*0.1;
    float dis=1.0-distance(fragCoord,mouse)/10.0;
    color+=vec4(clamp(dis*100.,0.0,1.0));
    #define sample 10
    vec2 ve[sample];
    for(int i=0;i< sample;i++){
        float angle=2.0*PI*float(i)/float(sample);
        vec2 offset=vec2(cos(angle),sin(angle))*3.;
        offset/=iResolution.xy;
        vec4 last = clamp(texture(fb,uv+offset),vec4(0.0),vec4(1.0));
        color+=(vec4(last.xyz,0))/float(sample);
        //color+=(vec4(last.x,0,0,0))/float(10);
        //color+=last/float(sample);

    }
    //color=vec4(uv,0.0,0.0);
	color*=vec4(.9);
    fragColor = clamp(color,vec4(0),vec4(1));
}
