float f_o_hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 o_trpino = (fragCoord.xy - iResolution.xy*0.5)/ iResolution.y;
    vec2 o_trpino_nooffset = (fragCoord.xy )/ iResolution.y;
    vec2 o_trmono = (iMouse.xy - iResolution.xy*0.5)/ iResolution.y;
    vec2 o_fc_nor = (fragCoord.xy )/ iResolution.xy;


    float[] a_n_operation_result = float[](
        0.0, //n_sum,    (n+=n); 
        1.0, //n_min     (min(n_min, n))
        1.0, //n_product (n*=n)
        1.0 //n_quotient
    );
    float n_index = floor(o_fc_nor.x / (1. / float(a_n_operation_result.length())));
    float n_x =(1./float(a_n_operation_result.length()));
    n_x = (n_x)*n_index +(n_x/2.);
    vec2[] a_o = vec2[](
        vec2(n_x, sin(iTime)*0.5+0.5),
        vec2(n_x, sin(iTime+(6.2831/2.))*0.5+0.5)
    );
    vec2 o_diff = vec2(0.);
    for(float n_index = 0.; n_index < float(a_o.length()); n_index+=1.){
        vec2 o_diff = o_fc_nor - a_o[int(n_index)];
        float n_length = length(o_diff);
        a_n_operation_result[0] += n_length;
        a_n_operation_result[1] = min(a_n_operation_result[1], n_length);
        a_n_operation_result[2] *= n_length;
        a_n_operation_result[3] /= n_length;
    }

    float n = a_n_operation_result[int(n_index)];
    fragColor = vec4(n);
    //fragColor = sqrt(fragColor);
}
