vec3 opRep( in vec3 p, in vec3 c)
{
    return mod(p+2.*c,c)-0.8*c;
}

float sdHexagram(vec3 p)
{
    p = opRep(p, vec3(15.));
    return length(p)-0.8;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 3.*(fragCoord/iResolution.xy)-1.;
	uv.x *= iResolution.x/iResolution.y;

    vec3 p = vec3 (iTime,2.,5.);
    vec3 dir = normalize(vec3(uv,3.));

    float shad = 1.;

    for (int i=0;i<60;i++)
    {
        float d = sdHexagram(p);
        if (d<0.001)
        {
            shad = float(i)/30.;
            break;
        }
        p += d*dir;
    }

    vec3 col = vec3(0.5 - shad);
    col += texture(iChannel0, uv*2.-iTime*0.3).rgb;
    fragColor = vec4(col,1.0);
}
