vec3 _colorize(float k, float n) {
    return vec3(pow(k, 1.2)/n, sin(k/n), .0);
}

void mainImage( out vec4 fragColor, in vec2 u )
{
    vec2  R = iResolution.xy,
          z = ( u+u - R ) / R.y;
    z = .9* abs( fract(2.3*z) - .5 );

    float k, n=75.;
    float time = iTime;
    for (k = 0.;k < n && dot(z,z) < 9.;k++)
        z = mat2(z,-z.y,z.x)*z 
          + vec2(- .95 + .2*cos(time*.1), +.25 + .08*sin(time) );

    fragColor = vec4(_colorize(k, n) - .2*fract(1e3*sin(1e3*dot(z,R-7.))), 1.0);
}
