#define iTime iTime
#define iResolution iResolution

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 st = fragCoord.xy / iResolution.xy;
	vec3 color = vec3(0.0);

	float numCircles = 8.0;
	float timeMultiplier = iTime * 2.0;
	float circleRadius = 0.1;
	float circleSpeed = 2.0;
	float circleSize = 0.05;

	for (float i = 0.0; i < numCircles; i += 1.0) {
		float angle = i * radians(360.0 / numCircles);
		vec2 circlePos = vec2(0.5, 0.5) + vec2(cos(angle), sin(angle)) * (0.5 - circleRadius);
		float dist = length(st - circlePos);

		float offset = sin(timeMultiplier * circleSpeed + i) * 0.5;
		float circle = step(circleSize + offset, dist);

		color += vec3(circle * sin(timeMultiplier * 4.0 + i), 
					  circle * cos(timeMultiplier * 2.0 + i), 
					  circle * sin(timeMultiplier * 6.0 + i));
	}

	color = mix(vec3(0.0), color, smoothstep(0.0, 1.0, st.y));

	fragColor = vec4(color, 1.0);
}
