#define PI 3.14159265359             
#define TWO_PI 6.28318530718

#define msin(x) sin(mod(x,TWO_PI))
#define mcos(x) cos(mod(x,TWO_PI))

float n(float t) {
	return fract(sin(mod(t*432.63,TWO_PI))*984.32); }


// ------------------------------------------------------

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

  vec2  uv = fragCoord/iResolution.xy;
        uv -= 0.5; uv.x *= iResolution.x/iResolution.y;

  float t = 115.0+iTime*0.2;

  vec2  uvs = uv;
        uvs.x += 0.04*msin(uvs.y*4.0-t*8.0);

  vec2  uvt = uvs*vec2(1.0,0.3)*0.8 + vec2(0.0,-t);
  float f = texture(iChannel0, uvt).r;

  float poleSize = 0.12 + msin(t*5.0)*0.02 + msin(uv.y*2.0+t*8.0)*0.02;
  vec3  col = vec3(0.1,0.7,1.0);
        col = mix(
          col, vec3(1.0), 
          (1.0-f*0.2) * min(1.0,
            (poleSize+msin((t+f)*7.0)*poleSize*f*0.1) / abs(uvs.x)
          ));

  float dotSize = 0.003;
  float dotRadius = 0.20;
  float div = 10.0;

        for (float i=0.0; i<1.0; i+=1.0/3.0) {
          float r = n(i*22.65)+0.5;
          vec2  uvr = uv;
          float id = n(1.72*floor((uvr.y-t*r)*div));
                uvr.y = mod(uvr.y-t*r, 1.0/div);
                r += id;
          dotRadius = poleSize*1.5 + poleSize*r;
          dotSize = 0.003;
          dotSize *= abs(uvr.x);
          dotSize *= (3.0 + 2.0*msin(t*r*50.0)*smoothstep(0.8,0.9,msin(t)) );
          vec2 ds = uvr-vec2( 
                msin((r*5.0 + t*id  )*PI)*dotRadius, 
                0.5/div);
               ds *= 15.0;
          col = mix(
            col, vec3(1.0),
            min(1.0,
              dotSize / dot( ds, ds
              )));
        }

    fragColor = vec4(col,1.0);
}
