#define pi 3.14159265359
#define tpi (2.0*pi)
#define color(offset) abs(sin(offset + vec3(3, 7, 11)*pi/6.0))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0.0);

    uv *= mat2(cos(0.5*iTime), sin(0.5*iTime), -sin(0.5*iTime), cos(0.5*iTime));

    float angle = mod(atan(uv.x, uv.y)/tpi, 1.0);
    float angle1 = mod(atan(abs(uv.x), abs(uv.y))/tpi, 1.0);

    for(float i = -0.3; i <= 0.5; i += 0.1)
    {
        float t = (texture(iChannel0, vec2(2.0*angle1/tpi)).x);
        col += color(3.0*iTime + 5.0*length(uv)) * 0.01/(length(uv) - t - 2.0*i);
    }

    fragColor = vec4(col, 1.0);
}
