#define pi 3.14159265359
#define tpi (2.0*pi)
#define color(offset) abs(sin(offset + vec3(3, 7, 11)*pi/6.0))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0.0);

    float angle = mod(atan(uv.x, uv.y)/tpi, 1.0);
    float angle1 = mod(atan(abs(uv.x*4.0), abs(uv.y*4.0))/tpi, 1.0);

    mat2 rot = mat2(cos(iTime), sin(iTime), -sin(iTime), cos(iTime));

    float numRings = min(ceil((iChannelTime[0]-0.65) * 9.0/40.0), 10.0);

    vec3 pallette = vec3(smoothstep(0.0, 0.65, iChannelTime[0]));
    if(iChannelTime[0] > 42.5 + 0.005*length(250.0 * uv.x * uv.y)) pallette = smoothstep(400.0, 398.0, iChannelTime[0])*color(3.0*(135.0/60.0)*iTime - 5.0*length(uv));

    for(float i = 0.0; i <= numRings/10.0; i += 0.1)
    {
        float t = (texture(iChannel0, vec2(angle1/10.0 + i)).x);
        col += (0.25*pallette) * 0.01/abs((length(uv) - i*t*smoothstep(400.0, 398.0, iChannelTime[0])));
    }

    fragColor = vec4(col, 1.0);
}
